import styles from "./ItemDetailsPage.module.css";
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import * as itemsService from "../../services/itemsService";

export const ItemDetailsPage = ({ addComment }) => {
  const { userId } = useParams();
  const [currentItem, setCurrentItem] = useState({});

  const [comment, setComment] = useState({
    username: "",
    comment: "",
  });

  const [error, setError] = useState({
    username: "",
    comment: "",
  });

  useEffect(() => {
    itemsService.getOne(userId).then((result) => {
      setCurrentItem(result);
    });
  }, []);

  const addCommentHandler = (e) => {
    e.preventDefault();

    const result = `${comment.username}: ${comment.comment}`;

    addComment(userId, result);
  };

  const onChange = (e) => {
    setComment((state) => ({
      ...state,
      [e.target.name]: e.target.value,
    }));
  };

  const validateUsername = (e) => {
    const username = e.target.value;
    let errorMessage = "";

    if (username.length < 4) {
      errorMessage = "Username must be longer than 4 characters";
    } else if (username.length > 10) {
      errorMessage = "Username must be shorter than 10 characters";
    }

    setError((state) => ({
      ...state,
      username: errorMessage,
    }));
  };
  return (
    <div className={styles.mainWrapper}>
      <div className={styles.innerWrapper}>
        <h1 className={styles.heading}>Item Details</h1>
        <h1>Item Type: {currentItem.type}</h1>
        <img className={styles.img} src={currentItem.img} />
        <h4>Size: {currentItem.size}</h4>
        <h4>Gender: {currentItem.gender}</h4>
        <h4>price: {currentItem.price}</h4>
        <h2>Created by: {currentItem.creator}</h2>
        <h2>Creator's phone number: {currentItem.phone}</h2>
        <div className={styles.btns}>
                    <Link to={`/items/${userId}/edit`} className={styles.btn}>
                        Edit
                    </Link>
                    <Link to="#" className={styles.btn}>
                        Delete
                    </Link>
                </div>
      </div>
    </div>
  );
};
